def create_adcreator
  # create ac_base
  ac_base = FactoryGirl.create(:ac_base)
  # create ac_creator_template

  ac_base.ac_creator_templates << FactoryGirl.create(:ac_creator_template, name: ac_base.name, title: ac_base.title, document_spec_xml: sample_document_spec_xml)
end

